CREATE TABLE IF NOT EXISTS `#__js_ticket_attachments` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`ticketid` int(11) DEFAULT NULL, 
	`replyattachmentid` int(11) DEFAULT NULL, 
	`filesize` varchar(32) DEFAULT NULL, 
	`filename` varchar(128) DEFAULT NULL, 
	`filekey` varchar(128) DEFAULT NULL, 
	`deleted` tinyint(1) DEFAULT NULL, 
	`status` tinyint(1) DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__js_ticket_config` (`configname` varchar(100) NOT NULL DEFAULT '', 
	`configvalue` TEXT DEFAULT NULL, 
	`configfor` varchar(50) DEFAULT NULL, PRIMARY KEY (`configname`), FULLTEXT KEY `config_name` (`configname`), FULLTEXT KEY `config_for` (`configfor`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;




CREATE TABLE IF NOT EXISTS `#__js_ticket_departments` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`emailtemplateid` int(11) DEFAULT NULL, 
	`emailid` int(11) DEFAULT NULL, 
	`autoresponceemailid` int(11) DEFAULT NULL, 
	`managerid` int(11) DEFAULT NULL, 
	`departmentname` varchar(32) DEFAULT NULL, 
	`departmentsignature` text DEFAULT NULL, 
	`ispublic` tinyint(1) DEFAULT NULL, 
	`isdefault` tinyint(1) DEFAULT NULL, 
	`sendemail` tinyint(1) DEFAULT NULL, 
	`ticketautoresponce` tinyint(1) DEFAULT NULL, 
	`messageautoresponce` tinyint(1) DEFAULT NULL, 
	`canappendsignature` tinyint(1) DEFAULT NULL, 
	`update` datetime DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	`status` tinyint(1) DEFAULT NULL, 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__js_ticket_email` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`autoresponce` tinyint(1) DEFAULT NULL, 
	`priorityid` int(11) DEFAULT NULL, 
	`departmentid` int(11) DEFAULT NULL, 
	`email` varchar(125) DEFAULT NULL, 
	`name` varchar(32) DEFAULT NULL, 
	`uid` int(11) DEFAULT NULL, 
	`password` varchar(125) DEFAULT NULL COMMENT '	', 
	`status` tinyint(1) DEFAULT NULL, 
	`smtpemailauth` TINYINT DEFAULT NULL,
	`mailhost` varchar(125) DEFAULT NULL, 
	`mailprotocol` enum('pop','map') DEFAULT NULL, 
	`mailencryption` enum('NONE','SSL') DEFAULT NULL, 
	`mailport` smallint(6) DEFAULT NULL, 
	`mailfetchfrequency` tinyint(3) DEFAULT NULL, 
	`mailfetchmaximum` tinyint(4) DEFAULT NULL, 
	`maildeleted` tinyint(1) DEFAULT NULL, 
	`mailerrors` tinyint(3) DEFAULT NULL, 
	`maillasterror` datetime DEFAULT NULL, 
	`maillastfetch` datetime DEFAULT NULL, 
	`smtpactive` tinyint(1) DEFAULT NULL, 
	`smtphosttype` INT DEFAULT NULL,
	`smtphost` varchar(125) DEFAULT NULL, 
	`smtpport` smallint(6) DEFAULT NULL, 
	`smtpsecure` tinyint(1) DEFAULT NULL, 
	`smtpauthencation` tinyint(1) DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	`update` datetime DEFAULT NULL, 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__js_ticket_emailtemplates` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`templatefor` varchar(50) DEFAULT NULL, 
	`title` varchar(50) DEFAULT NULL, 
	`subject` varchar(255) DEFAULT NULL, 
	`body` text DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	`status` tinyint(1) DEFAULT NULL, 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=25 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__js_ticket_priorities` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`priority` varchar(60) DEFAULT NULL, 
	`prioritycolour` varchar(7) DEFAULT NULL, 
	`priorityurgency` int(1) DEFAULT NULL, 
    `overduetypeid` int(11) DEFAULT NULL,
    `overdueinterval` int(11) DEFAULT NULL, 
	`ispublic` varchar(45) DEFAULT NULL, 
	`isdefault` tinyint(1) DEFAULT NULL, 
	`status` tinyint(4) NOT NULL DEFAULT '0', 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__js_ticket_replies` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`ticketid` int(11) DEFAULT NULL, 
	`name` varchar(50) DEFAULT NULL, 
	`message` text DEFAULT NULL, 
	`staffid` int(11) DEFAULT NULL, 
	`rating` enum('1','5') DEFAULT NULL, 
	`status` tinyint(1) DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	`ticketviaemail` tinyint(1) NOT NULL DEFAULT '0', 
	`mergemessage` TINYINT(1) NOT NULL DEFAULT '0',
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__js_ticket_system_errors` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`uid` int(11) DEFAULT NULL, 
	`error` text DEFAULT NULL, 
	`isview` tinyint(1) DEFAULT '0', 
	`created` datetime DEFAULT NULL, 
	PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__js_ticket_tickets` (
	`id` int(11) NOT NULL AUTO_INCREMENT, 
	`uid` int(11) DEFAULT NULL, 
	`ticketid` varchar(25) DEFAULT NULL, 
	`departmentid` int(11) DEFAULT NULL, 
	`priorityid` int(11) DEFAULT NULL, 
	`staffid` int(11) DEFAULT NULL, 
	`email` varchar(255) DEFAULT NULL, 
	`name` varchar(100) DEFAULT NULL, 
	`subject` varchar(255) DEFAULT NULL, 
	`message` text DEFAULT NULL, 
	`helptopicid` int(11) DEFAULT NULL, 
	`phone` varchar(100) DEFAULT NULL, 
	`phoneext` varchar(20) DEFAULT NULL, 
	`status` tinyint(1) DEFAULT NULL, 
	`isoverdue` tinyint(1) DEFAULT NULL, 
	`isanswered` tinyint(1) NOT NULL DEFAULT '0', 
	`duedate` datetime DEFAULT NULL, 
	`reopened` datetime DEFAULT NULL, 
	`closed` datetime DEFAULT NULL, 
	`lastreply` datetime DEFAULT NULL, 
	`created` datetime DEFAULT NULL, 
	`update` datetime DEFAULT NULL, 
	`lock` tinyint(4) NOT NULL DEFAULT '0', 
	`ticketviaemail` tinyint(1) NOT NULL DEFAULT '0',
	`ticketviaemail_id` INT(11) DEFAULT NULL,	
	`attachmentdir` varchar(50) NOT NULL,
	`feedbackemail` TINYINT DEFAULT NULL,
	`mergestatus` TINYINT(4) NOT NULL DEFAULT '0',
	`mergewith` INT(11) DEFAULT NULL,
	`mergenote` TEXT DEFAULT NULL,
	`mergedate` datetime DEFAULT NULL,
	`multimergeparams` TEXT DEFAULT NULL,
	`mergeuid` INT(11) DEFAULT NULL,
	`params` longtext DEFAULT NULL,
	`hash` varchar(200) COLLATE 'utf8_general_ci' DEFAULT NULL,
	PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__js_ticket_fieldsordering` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `field` varchar(50) NOT NULL,
  `fieldtitle` varchar(50) DEFAULT NULL,
  `ordering` int(11) DEFAULT NULL,
  `section` varchar(20) DEFAULT NULL,
  `fieldfor` tinyint(2) DEFAULT NULL,
  `published` tinyint(1) DEFAULT NULL,
  `sys` tinyint(1) NOT NULL DEFAULT '0',
  `cannotunpublish` tinyint(1) NOT NULL DEFAULT '0',
  `required` tinyint(1) NOT NULL DEFAULT '0',
  `isuserfield` TINYINT DEFAULT NULL,
  `depandant_field` VARCHAR( 250 ) DEFAULT NULL,
  `showonlisting` TINYINT DEFAULT NULL,
  `search_user` TINYINT DEFAULT NULL,
  `userfieldparams` LONGTEXT DEFAULT NULL,
  `userfieldtype` VARCHAR( 250 ) DEFAULT NULL,
  `size` VARCHAR( 200 ) DEFAULT NULL,
  `maxlength` VARCHAR( 200 ) DEFAULT NULL,
  `cols` VARCHAR( 200 ) DEFAULT NULL,
  `rows` VARCHAR( 200 ) DEFAULT NULL,
  `isvisitorpublished` TINYINT DEFAULT NULL,
  `search_visitor` TINYINT DEFAULT NULL,
  `cannotsearch` TINYINT DEFAULT NULL,
  `cannotshowonlisting` TINYINT DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fieldordering_filedfor` (`fieldfor`)
) ENGINE=MyISAM AUTO_INCREMENT=25 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__js_ticket_erasedatarequests` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL,
  `subject` varchar(250) NOT NULL,
  `message` text NOT NULL,
  `status` int(11) NOT NULL,
  `created` datetime NOT NULL,
  PRIMARY KEY (`id`) 
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
