INSERT INTO `#__js_ticket_emailtemplates` (`id`, `templatefor`, `title`, `subject`, `body`, `created`, `status`) VALUES
(1, 'ticket-new', '', 'JS Support Ticket: New Ticket Received [{TRACKINGID}]', '<div style=\"background-color: #f7f7f7; margin: 0; padding: 70px 0; width: 100%;\">\r\n<div style=\"border: 3px dotted #ebecec; width: 600px; display: block; margin: 0 auto; background: #fff;\">\r\n<div style=\"float: left; width: 100%; background: #576cf1; background-repeat: no-repeat, no-repeat, no-repeat; background-position: top left, top right, center center; margin-bottom: 20px;\">\r\n<div style=\"padding: 22px 20px; background: #3e4095; color: #fff; font-size: 18px; font-weight: bold; text-transform: capitalize;\">\r\n<div style=\"display: inline-block;\">JS Support Ticket</div>\r\n<div style=\"float: right; font-weight: bold; font-size: 18px; color: #fff;\">Dear {USERNAME},</div>\r\n</div>\r\n<div style=\"padding: 40px 30px; text-align: center; font-weight: bold; color: #fff; text-transform: capitalize; font-size: 22px;\">New Ticket Received</div>\r\n</div>\r\n<div style=\"padding: 0px;\">\r\n<div style=\"padding-bottom: 20px; border-bottom: 1px solid #dbdbda; background: #f9f9f9; color: #4b4b4d; margin-bottom: 40px;\">\r\n<div style=\"color: #727376; line-height: 2; padding: 17px; text-align: center;\">Your support ticket <strong>{SUBJECT}</strong> has been submitted. We try to reply to all tickets as soon as possible, usually within 24 hours.</div>\r\n</div>\r\n<div style=\"padding: 20px 20px 10px; border-top: 1px solid #dbdbda; border-bottom: 1px solid #dbdbda; background: #f9f9f9;\">\r\n<div style=\"font-weight: bold; font-size: 16px; margin-bottom: 10px; color: #4b4b4d;\">Tracking ID: <span class=\"js-email-paramater\">{TRACKINGID}</span></div>\r\n</div>\r\n<div style=\"padding: 20px 20px; border-top: 1px solid #dbdbda; border-bottom: 1px solid #dbdbda;\">Email ID: {EMAIL}</div>\r\n<div style=\"padding: 20px 20px;\">Ticket message: {MESSAGE}</div>\r\n<div style=\"padding: 20px 20px; border-top: 1px solid #dbdbda; border-bottom: 1px solid #dbdbda;\">You can view the status of your ticket here:<br />{TICKETURL}</div>\r\n<div style=\"padding: 20px 20px; border-bottom: 1px solid #dbdbda;\">You will receive an e-mail notification when our staff replies to your ticket.</div>\r\n<div style=\"background: #fef2ef; padding: 15px; margin: 20px; border: 1px solid #eba7a8;\">\r\n<div style=\"font-weight: bold; font-size: 14px; margin-bottom: 5px; color: #983133; text-transform: uppercase;\">Do not reply TO this E-Mail</div>\r\n<div style=\"color: #727376; line-height: 2;\">This is an automated e-mail message sent from our support system. Do not reply to this e-mail as we cannot receive your reply!</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2012-07-20 11:09:00', 1),
